package org.bitvector.microservice2;import akka.actor.ActorRef;import akka.actor.ActorSystem;import akka.actor.Inbox;import akka.actor.Props;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import java.io.IOException;import java.io.InputStream;import java.util.Properties;public class Main {    public static void main(String[] args) {        Logger logger = LoggerFactory.getLogger(Main.class.getName());        logger.info("Starting Initialization");        String propertiesFile = "microservice2.properties";        ClassLoader loader = Thread.currentThread().getContextClassLoader();        try (InputStream resourceStream = loader.getResourceAsStream(propertiesFile)) {            if (resourceStream != null) {                Properties props = new Properties(System.getProperties());                props.load(resourceStream);                System.setProperties(props);            } else {                logger.error("Could not load properties file: " + propertiesFile);            }        } catch (IOException e) {            logger.error("Could not load properties file: " + propertiesFile, e);        }        ActorSystem system = ActorSystem.create("TheTheatre");        Inbox inbox = Inbox.create(system);        ActorRef httpActor = system.actorOf(Props.create(HttpActor.class), "HttpActor");        inbox.watch(httpActor);        inbox.send(httpActor, new HttpActor.Start(                Integer.parseInt(System.getProperty("bitvector.microservice2.listen-port")),                System.getProperty("bitvector.microservice2.listen-address")        ));        ActorRef dbActor = system.actorOf(Props.create(DbActor.class), "DbActor");        inbox.watch(dbActor);        inbox.send(dbActor, new DbActor.Start());        logger.info("Finished Initialization");    }}